import { Component } from 'react';
import OrderItem from "../../components/order-item/OrderItem";
import { Constants } from "../../Constants";
import "./UserInProgress.css";
import { GridList } from "@material-ui/core";

export default class UserProgress extends Component {

    state = {
        orders: Constants.getToLocalStorage(Constants.ORDER) === null ? [] : Constants.getToLocalStorage(Constants.ORDER),
        user: Constants.getToLocalStorage(Constants.LOGGED_IN_USER),
        orderList: [],
        orderedItem: {
            id: 0,
            productId: 0,
            quantity: 0,
            status: '',
            userId: 0
        }
    }

    constructor() {
        super();
        const orders = { ...this.state }
        orders.orderList = this.state.orders.filter(order =>
            order.userId === this.state.user.id && (order.status === Constants.STATUS_PENDING || order.status === Constants.STATUS_TO_RECEIVE)
        ).map(item => {
            const product = { ...this.state.orderedItem }
            product.id = item.id;
            product.productId = item.productId;
            product.quantity = item.quantity;
            product.status = item.status;
            product.userId = item.userId;

        }
        );
    }

    render() {
        return (
            <div className="progress-container">
                <GridList cols={3} cellHeight={250} style={{ height: 'auto' }} >
                    {
                        this.state.orders.map(order =>
                            <ul key={order.id}>
                                <OrderItem key={order.title} imageUrl={order.imageUrl} onClickViewOrder={this.props.onClickViewOrder} />
                            </ul>
                        )
                    }
                </GridList>
            </div>
        );
    }
}
