import { Component } from "react";
import { Constants } from "../../Constants";
import "./ItemDetails.css";
import { User } from "../../model/user"

class ItemDetails extends Component {

    state = {
        user: <User />,
        order: {
            id: 0,
            productId: 0,
            userId: 0,
            quantity: 0,
            status: "pending",
            image: '',
            productName: '',
            price: 0,
            deliveryAddress: '',
            sellerName: '',
            sellerAddress: ''
        }
    }

    constructor() {
        super();
        this.state.user = Constants.getToLocalStorage(Constants.LOGGED_IN_USER);
    }

    onInputChange(quantity) {
        const userState = { ...this.state };
        userState.order.quantity = quantity;
        this.setState(userState);
    }

    displayDetails(value) {
        if (this.props.buyProduct != null) {
            return this.props + '.' + value;
        } else if (this.productId.viewProduct != null) {
            return this.productId.viewProduct + '.' + value;
        }
    }

    onClickOrder() {
        if (this.state.order.quantity <= 0) {
            alert('Please add quantity.');
            return;
        }
        const order = { ...this.state.order };
        order.id = Constants.getToLocalStorage(Constants.ORDER) === null ? 1 : Constants.getToLocalStorage(Constants.ORDER).length + 1;
        order.productId = this.props.buyProduct.id;
        order.userId = this.state.user.id;
        order.status = Constants.STATUS_PENDING;
        order.image = this.props.buyProduct.image;
        order.productName = this.props.buyProduct.productName;
        order.price = this.props.buyProduct.productPrice;
        order.deliveryAddress = this.state.user.address;
        order.sellerName = this.props.buyProduct.sellerName;
        order.sellerAddress = this.props.buyProduct.sellerAddress;
        this.props.orderOnClick(order);
    }

    render() {
        return (
            <div className="details-container">
                <img alt="" className="item-details-image"
                    src={this.displayDetails('image')}></img>
                <p className="details-format">{this.props.buyProduct.productName}</p>
                <p className="details-format">Price : P {this.props.buyProduct.productPrice}</p>
                <p className="details-format">Quantity : <input onChange={(e) => this.onInputChange(e.target.value)} type="number"></input></p>
                <p className="details-format">Total : {this.props.buyProduct.productPrice * this.state.order.quantity}</p>
                <p className="details-format">Deliver Address : {this.state.user.address}</p>
                <br />
                <p className="details-format">Seller info</p>
                <p className="details-format">Name : {this.props.buyProduct.sellerName}</p>
                <p className="details-format">Pick-up Address : {this.props.buyProduct.sellerAddress}</p>

                <button onClick={() => this.onClickOrder()} className={"button-buy"}>Buy</button>

            </div>
        );
    }
}

export default ItemDetails;
