      return <LoggedInUser />
    } else {
      return <DashBoard />
    }
  }

  return (
    <div className="main-container">
      <div className="center-container">
        {
          isUserLoggedIn()
        }
        <div className="login-container">
          <a onClick={loginOnClick} className="login-style">Login</a>
          <p className="login-style"> / </p>
          <a onClick={registerOnClick} className="login-style">Sign up</a>
        </div>
      </div>

      <Dialog open={open} onClose={handleClose}>
        {
          type === LOGIN ? <Login handleClose={handleClose} /> : <Register handleClose={handleClose} />
        }
      </Dialog>
    </div>
  );
}

export default App;
